9db9720107cef99189470fd0988bb66d02e8379a.source:
query GetFollowingActivities($first: Int, $after: String) {
  viewer {
    __typename
    followingActivities(
      first: $first
      after: $after
      orderBy: {field: CREATED_AT, direction: DESC}
    ) {
      __typename
      edges {
        __typename
        cursor
        node {
          __typename
          ...ActivityItemFragment
        }
      }
      pageInfo {
        __typename
        ...PageInfoFragment
      }
    }
  }
}
fragment ActivityItemFragment on ActivityItem {
  __typename
  ... on Record {
    ...RecordFragment
  }
  ... on Review {
    ...ReviewFragment
  }
  ... on Status {
    ...StatusFragment
  }
  ... on MultipleRecord {
    id
  }
}
fragment RecordFragment on Record {
  __typename
  id
  annictId
  comment
  ratingState
  createdAt
  user {
    __typename
    ...UserFragment
  }
  work {
    __typename
    ...WorkFragment
  }
  episode {
    __typename
    ...EpisodeFragment
  }
}
fragment UserFragment on User {
  __typename
  id
  name
  username
  avatarUrl
  description
}
fragment WorkFragment on Work {
  __typename
  id
  annictId
  title
  episodesCount
  watchersCount
  reviewsCount
  seasonName
  seasonYear
  viewerStatusState
  media
}
fragment EpisodeFragment on Episode {
  __typename
  id
  annictId
  title
  numberText
  viewerRecordsCount
}
fragment ReviewFragment on Review {
  __typename
  id
  annictId
  title
  body
  ratingAnimationState
  ratingOverallState
  ratingMusicState
  ratingStoryState
  ratingCharacterState
  createdAt
  user {
    __typename
    ...UserFragment
  }
  work {
    __typename
    ...WorkFragment
  }
}
fragment StatusFragment on Status {
  __typename
  id
  annictId
  createdAt
  state
  user {
    __typename
    ...UserFragment
  }
  work {
    __typename
    ...WorkFragment
  }
}
fragment PageInfoFragment on PageInfo {
  __typename
  hasNextPage
  hasPreviousPage
  endCursor
  startCursor
}

